import React from 'react'
import "../Cart/Cart.css"
import cart from "../../../Images/cart.png"

function Cart(props) {
    const {cartItems,onAdd,onRemove}=props
    const {itemsprice}= cartItems.reduce((a,c)=>a+c.Price*c.qty,0)
    const {itemqty}=cartItems.reduce((a,c)=>a+c.qty,0)
  return (
    <div>
        <div className="m3" style={{display: cartItems.length===0? 'block': 'none'}}>
       <h1>CART EMPTY</h1>
       <div className="ima">
           <img className="img" src={cart} alt="Cart pic"/>
       </div>
       <div className="good">
           <p>Good food is always cooking, go ahead and order some items from the menu.</p>
       </div>
    </div>
    <div className="m4" style={{display:cartItems.length!=0 ? 'block':'none'}}>
        <h1>Cart</h1>
        <p>{itemqty}</p>
        {
            cartItems.map((item)=>{
                return(
                    <div key={item.id} className='cart-row'> 
                        <span></span>{item.Name}
                        <button onClick={()=>onAdd(item)} className='add'>+plus</button>
                        <p>{item.qty}</p>
                        <button onClick={()=>onRemove(item)} className='Remove'>-Minus</button>
                        <div>
                            Rs. {item.Price * item.qty} 
                        </div>

                    </div>

                )
            })

        }
        <div>
            <span>Subtotal : {itemsprice}</span>
        </div>
    </div>
    </div>
  )
}

export default Cart
